create table appointment (appointment_time timestamp(6), doctor_id bigint not null, id bigint generated by default as identity, patient_id bigint not null, reason varchar(255), primary key (id));
create table doctor (latitude float(53), longitude float(53), id bigint generated by default as identity, area varchar(255), contact_details varchar(255), formatted_address varchar(255), name varchar(255), password varchar(255), social_security_number varchar(255), specialty varchar(255), primary key (id));
create table patient (id bigint generated by default as identity, contact_details varchar(255), name varchar(255), password varchar(255), social_security_number varchar(255), primary key (id));
create table working_hours (end_time time(6), start_time time(6), doctor_id bigint not null, id bigint generated by default as identity, day_of_week varchar(255) check (day_of_week in ('MONDAY','TUESDAY','WEDNESDAY','THURSDAY','FRIDAY','SATURDAY','SUNDAY')), primary key (id));
